import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import './App.css';
// import 'react-calendar/dist/Calendar.css';

class Event {
  constructor(name, date, time, location) {
    this.name = name;
    this.date = date;
    this.time = time;
    this.location = location;
  }
}

function UpcomingEvents({ eventsByDate }) {  // Corrected function signature
  const lastEvents = eventsByDate.slice(-2); // Fixed lastEvents assignment

  return (
    <div>
      {lastEvents.map((upcomingEvent, index) => (  // Fixed map iteration and key
        <div key={index}>
          <h1>Name: {upcomingEvent.name}</h1>
          <p>
            Date: {upcomingEvent.date.toDateString()} {/* toDateString() for better formatting */}
            <br />Location: {upcomingEvent.location}
          </p>
        </div>
      ))}
    </div>
  );
}

function App() {
  const [events, setEvents] = useState([
    new Event('Lecture', new Date(2024, 8, 4), '10:00', 'Aula 3-3'),
    new Event(`Programmer's Day`, new Date(2024, 8, 14), '17:00', 'Tekwill')
  ]);

  const [calendarEvents, setCalendarEvents] = useState({});

  useEffect(() => {
    // Group events by date
    const eventsByDate = events.reduce((accumulation, event) => {
      const dateKey = event.date.toDateString(); // Convert date to a string for the key
      if (!accumulation[dateKey]) {
        accumulation[dateKey] = [];
      }
      accumulation[dateKey].push(event); // Add event to the array for this date
      return accumulation;
    }, {});

    setCalendarEvents(eventsByDate); // Update the state with the grouped events
  }, [events]); // Re-run when `events` changes

  // Function to render events for a given date
  const tileContent = ({ date, view }) => {
    const dateKey = date.toDateString();
    const eventsForDate = calendarEvents[dateKey];

    return view === 'month' && eventsForDate ? (
      <div>
        {eventsForDate.map((event, index) => (
          <div key={index} className="event-css"> {/* Changed class to className */}
            <strong>{event.name}</strong> <div>{event.time}, {event.location}</div>
          </div>
        ))}
      </div>
    ) : null;
  };

  return (
    <div className="gradient-background"> {/* Changed class to className */}
      <div className="calendar-and-upc-events-css"> {/* Changed class to className */}
        <div>
          <h1>Upcoming Events</h1>
          <UpcomingEvents eventsByDate={calendarEvents} /> {/* Corrected function call */}
        </div>
        <div className="search-calendar"> {/* Changed class to className */}
          <div className="search-icon-text-css"> {/* Changed class to className */}
            <div>Calendar</div>
            <div><button><img src="C:\Users\maria\Desktop\ReactJS\my-app\images\search_icon.png" alt="search icon" /></button></div> {/* Added alt attribute */}
          </div>
          <div>
            <Calendar tileContent={tileContent} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
