// App.js
import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import UpcomingEvents from './UpcomingEvents'; // Import the new component
import './App.css';

class Event {
  constructor(name, date, time, location) {
    this.name = name;
    this.date = date;
    this.time = time;
    this.location = location;
  }
}

function App() {
  const [events, setEvents] = useState([
    new Event('Lecture', new Date(2024, 8, 4), '10:00', 'Aula 3-3'),
    new Event(`Programmer's Day`, new Date(2024, 8, 14), '17:00', 'Tekwill')
  ]);

  const [calendarEvents, setCalendarEvents] = useState({});
  const [upcomingEvents, setUpcomingEvents] = useState([]);

  useEffect(() => {
    // Group events by date
    const eventsByDate = events.reduce((accumulation, event) => {
      const dateKey = event.date.toDateString();
      if (!accumulation[dateKey]) {
        accumulation[dateKey] = [];
      }
      accumulation[dateKey].push(event);
      return accumulation;
    }, {});

    setCalendarEvents(eventsByDate);

    // Get the last 2 events
    const lastTwoEvents = events.slice(-2);
    setUpcomingEvents(lastTwoEvents);
  }, [events]);

  // Function to render events for a given date in the calendar
  const tileContent = ({ date, view }) => {
    const dateKey = date.toDateString();
    const eventsForDate = calendarEvents[dateKey];

    if (view === 'month' && eventsForDate) {
      return (
        <div>
          {eventsForDate.slice(-2).map((event, index) => (
            <div key={index} className="event-css">
              <strong>{event.name}</strong>
              <div>{event.time}, {event.location}</div>
            </div>
          ))}
        </div>
      );
    }

    return null;
  };

  return (
    <div className="calendar-and-upc-events-css">
      <div><h1>Upcoming Events</h1></div>
      <div className="search-calendar">
        <div className="search-icon-text-css">
          <div>Calendar</div>
          <div><button img src="" /></div>
        </div>
        <div><Calendar tileContent={tileContent} /></div>
      </div>
      {/* Render the UpcomingEvents component */}
      <UpcomingEvents events={upcomingEvents} />
    </div>
  );
}

export default App;
